import React, { Component } from 'react';
import axios from 'axios';

export default class Call extends Component {
    constructor(props) {
        super(props);
        this.state = {
          data: [{}]
        }
  }

  updateSearch(event){
    this.setState({search: event.target.value.substr(0,20)})
}

    componentDidMount() {
        {/* call the api on page load */}
        this.callApi();
    }
    async callApi() {
        {/*https://medium.com/@chaoyue_zhao/how-to-make-axios-api-calls-with-yelp-fusion-inside-react-js-10755d8485c5*/}

        axios.get(`${'https://cors-anywhere.herokuapp.com/'}https://api.yelp.com/v3/businesses/search`, {
        headers: {
            Authorization: `Bearer ${process.env.REACT_APP_API_KEY}`
        },
        params: {
            term: this.props.term,
            latitude: '40.730610',
            longitude: '-73.935242',
        }
        })
        .then((res) => {
        console.log(res.data.businesses);
        })
        .catch((err) => {
        console.log ('error');
        })

      }

  render() {
    return (
      <div className="holder"> 
      {/* <hr/> */}
        <form onSubmit={this.submitHandler}>
            <div className="search-tag" htmlFor="search-input">
                {/* <ul>
                  {this.props.name.map((name))z}
                </ul> */}


                <input 
                type= "text" 
                name= "name" 
                id ="search-input"
                value= {this.state.search}
                placeholder = "search"
                onChange= {this.updateSearch.bind(this)}
                />
                  <button className="button" type="submit"> Submit</button>

                  <h1 className="caption"> Search for a restaurant that delivers in New York</h1>
                  </div>
              </form>
            </div>
    )
  }
}
