import React, { Component } from 'react';
import '../styles/Search.css';
import Call from './Call';

class Search extends Component {
        constructor (){
        super ();
        this.state ={
        search: '',
        renderReview:false,
        submitSearch:<div id="searchheader">Please submit a search</div>,
        };

        this.updateSearch = this.updateSearch.bind(this)
        this.makeSearch = this.makeSearch.bind(this);
        }
updateSearch(event){
  event.preventDefault();
    this.setState({search: event.target.value})
}
makeSearch(event){
  event.preventDefault();
  this.setState({renderReview:true,submitSearch:<Call capture={this.props.capture} term={this.state.search}/>});
}

   render (){


     return(


        <div className="holder"> 
        {/* <hr/> */}

          {/* <form onSubmit={this.makeSearch}> */}
              <div className="search-tag" >
                  <input 
                  type= "text" 
                  name= "name" 

                  placeholder = "search"
                  onChange= {this.updateSearch}
                  />

                  {/* <hr/> */}
                  <button className="button_sea" type="submit" name="Submit" onClick={this.makeSearch} /> 

                  {/* Button is used to submit the request */}
                  {/* <h1 className="caption">search for a restaurant that delivers in San Francisco</h1>   */}

              </div>
         {/* </form> */}
          {/* move this back later this.state.renderReview==false*/ }
          {/* <br/>


           */}
           <br/>
          <br/>
          <br/>
          <div>
          {this.state.submitSearch}
          {/*this.state.renderReview ==true &&	<Call term="delis"/>*/}	          
          {/*this.state.renderReview ==true &&	<ReviewCall restuarant="prospect-san-francisco" />*/}

          </div>
        </div>
     )
     }
}

export default Search;
